SELECT     a.COB_DATE,     a.CURRENCY_OF_MEASURE,     SUM(a.USD_EQ_KAPPA) AS USD_EQ_KAPPA FROM     CDWUSER.U_EQ_MSR_INTRPLT a WHERE  A.CCC_PL_REPORTING_REGION in ('JAPAN') AND      (a.VAR_EXCL_FL <> 'Y') AND     (a.ccc_business_area <> 'INTERNATIONAL WEALTH MGMT') AND     a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and     a.CCC_BANKING_TRADING = 'TRADING' GROUP BY     A.COB_DATE,     a.CURRENCY_OF_MEASURE