SELECT      a.COB_DATE,      a.ISSUER_COUNTRY_CODE_DECOMP,      SUM (a.USD_EQ_DELTA_DECOMP) AS USD_EQ_DELTA FROM CDWUSER.U_DECOMP_MSR a      WHERE      1 = 1 AND (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and A.CCC_PL_REPORTING_REGION in ('JAPAN') AND       a.CCC_DIVISION = 'FIXED INCOME DIVISION' AND     /*a.CCC_BUSINESS_AREA NOT IN ('COMMODITIES') AND*/       CCC_BANKING_TRADING IN ('TRADING') AND     a.ULTIMATE_CREDIT_PARTY_DARWIN_NAME NOT LIKE ('%MORGAN STANLEY%') AND      a.USD_EQ_DELTA_DECOMP IS NOT NULL GROUP BY      a.COB_DATE,      a.ISSUER_COUNTRY_CODE_DECOMP