SELECT     a.COB_DATE,     a.CURRENCY_OF_MEASURE,     SUM (a.USD_FX) AS USD_FX FROM DWUSER.U_FX_MSR a WHERE     1 = 1 AND (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and     ( A.CCC_PL_REPORTING_REGION in ('JAPAN') AND      1=1) AND     ((a.CCC_DIVISION = 'FIXED INCOME DIVISION' AND       a.CCC_BUSINESS_AREA = 'CREDIT-CORPORATES' AND       a.CCC_PRODUCT_LINE = 'DISTRESSED TRADING') OR      a.CCC_DIVISION = 'INSTITUTIONAL SECURITIES OTHER') AND     (a.VAR_EXCL_FL <> 'Y') GROUP BY     a.COB_DATE,     a.CURRENCY_OF_MEASURE