SELECT      a.COB_DATE,      a.CURRENCY_OF_MEASURE,      sum(a.USD_IR_UNIFIED_PV01) as SUM_USD_IR_UNIFIED_PV01 FROM cdwuser.U_IR_MSR a WHERE      1 = 1 AND A.CCC_PL_REPORTING_REGION in ('JAPAN') AND       (a.VAR_EXCL_FL <> 'Y' AND      a.MEASURE_VAR_EXCL_FL <> 'Y') AND      (a.ccc_business_area <> 'INTERNATIONAL WEALTH MGMT') AND      a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND (a.COB_DATE = '2018-02-28') and      a.CCC_BANKING_TRADING = 'TRADING' GROUP BY      a.COB_DATE,      a.CURRENCY_OF_MEASURE