WITH fid_ir_kappa AS(     SELECT         a.COB_DATE,         a.VERTICAL_SYSTEM,         a.CCC_DIVISION,         a.CCC_BUSINESS_AREA,         a.CCC_PRODUCT_LINE,         a.CCC_STRATEGY,         a.CCC_PL_REPORTING_REGION,         a.CURRENCY_OF_MEASURE,         a.TERM_NEW,         a.USD_IR_KAPPA / 10 AS USD_IR_KAPPA     FROM         cdwuser.U_IR_MSR_INTRPLT a     WHERE         1 = 1 AND         a.CCC_BANKING_TRADING = 'TRADING' AND (a.COB_DATE = '2018-02-28') and         ((1 = 1 AND         ( A.CCC_PL_REPORTING_REGION in ('JAPAN') AND          1 = 1)) OR         a.BOOK IN ('FXJVILDDIC')) AND         (a.VAR_EXCL_FL <> 'Y') AND         a.USD_IR_KAPPA IS NOT NULL AND         a.CCC_DIVISION IN ('FIXED INCOME DIVISION', 'BANK RESOURCE MANAGEMENT') ), fid_ir_kappa_summary AS(     SELECT         COB_DATE,         VERTICAL_SYSTEM,         CURRENCY_OF_MEASURE,         SUM (USD_IR_KAPPA) AS USD_IR_KAPPA     FROM         fid_ir_kappa     GROUP BY         COB_DATE,         VERTICAL_SYSTEM,         CURRENCY_OF_MEASURE ) SELECT     * FROM     fid_ir_kappa_summary