SELECT     a.COB_DATE,     a.CURRENCY_OF_MEASURE,     SUM (a.USD_FX) AS SUM_USD_FX FROM cdwuser.U_FX_MSR a WHERE     1 = 1 AND (a.COB_DATE = '2018-02-28') and A.CCC_PL_REPORTING_REGION in ('JAPAN') AND        CCC_BANKING_TRADING IN ('TRADING') AND     a.VAR_EXCL_FL <> 'Y' AND     a.MEASURE_VAR_EXCL_FL <> 'Y' AND     (a.ccc_business_area <> 'INTERNATIONAL WEALTH MGMT') AND     a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND     a.usd_FX IS NOT NULL GROUP BY     a.COB_DATE,     a.CURRENCY_OF_MEASURE