SELECT a.COB_DATE,     CASE          WHEN a.TERM_NEW <= 1 THEN '0-1y'         WHEN a.TERM_NEW > 1 AND a.TERM_NEW <= 5 THEN '1-5y'         WHEN a.TERM_NEW > 5 AND a.TERM_NEW <= 10 THEN '5-10y'         WHEN a.TERM_NEW > 10 AND a.TERM_NEW <= 15 THEN   '10-15y'     ELSE '15+y' END AS TERM_NEW_GROUP, CASE WHEN a.PRODUCT_TYPE_CODE IN ('GVTBOND', 'GVTBONDIL', 'GVTFRN') THEN 'JGB_CASH' WHEN a.PRODUCT_TYPE_CODE IN ('BONDFUT', 'BONDFUTOPT') THEN 'JGB_FUT' ELSE 'JGB_OTHERS' END AS CURVE_NAME, SUM(a.USD_IR_UNIFIED_PV01) AS USD_IR_UNIFIED_PV01 FROM cdwuser.U_IR_MSR_INTRPLT a WHERE       CCC_BANKING_TRADING IN ('TRADING') AND (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and (( A.CCC_TAPS_COMPANY in ('0362','0816') AND  1=1 ) OR a.BOOK IN ('FXJVILDDIC')) AND /*(a.PRODUCT_TYPE_CODE IN ('BONDFUT', 'BONDFUTOPT') OR (a.VAR_EXCL_FL <> 'Y' AND a.MEASURE_VAR_EXCL_FL <> 'Y')) AND*/     a.CCC_DIVISION IN ('FIXED INCOME DIVISION') AND     /*a.CCC_BUSINESS_AREA NOT IN ('COMMODITIES') AND*/ a.PRODUCT_TYPE_CODE IN ('BONDFUT', 'BONDOPT', 'GVTBOND', 'GVTBONDIL', 'GVTFRN', 'GOVTBONDOPT', 'BONDFUTOPT') AND a.CURRENCY_OF_MEASURE = 'JPY' AND a.CURVE_TYPE<>'FXBASISYIELDCURVE' AND a.USD_IR_UNIFIED_PV01 IS NOT NULL GROUP BY a.COB_DATE,     CASE          WHEN a.TERM_NEW <= 1 THEN '0-1y'         WHEN a.TERM_NEW > 1 AND a.TERM_NEW <= 5 THEN '1-5y'         WHEN a.TERM_NEW > 5 AND a.TERM_NEW <= 10 THEN '5-10y'         WHEN a.TERM_NEW > 10 AND a.TERM_NEW <= 15 THEN   '10-15y'     ELSE '15+y' END, CASE WHEN a.PRODUCT_TYPE_CODE IN ('GVTBOND', 'GVTBONDIL', 'GVTFRN') THEN 'JGB_CASH' WHEN a.PRODUCT_TYPE_CODE IN ('BONDFUT', 'BONDFUTOPT') THEN 'JGB_FUT' ELSE 'JGB_OTHERS' END HAVING SUM(a.USD_IR_UNIFIED_PV01) <> 0