SELECT      a.COB_DATE AS VARDATE,      a.CURRENCY_OF_MEASURE,     SUM(USD_FX) AS USD_FX FROM     cdwuser.u_fx_msr a  WHERE     (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and A.CCC_TAPS_COMPANY in ('0362','0816') AND      A.CCC_DIVISION IN ('INSTITUTIONAL EQUITY DIVISION')  AND      (a.CCC_BUSINESS_AREA <> 'INTERNATIONAL WEALTH MGMT') AND        CCC_BANKING_TRADING IN ('TRADING') group by     COB_DATE,     a.CURRENCY_OF_MEASURE