select ccc_pl_reporting_region, Case when parent_legal_entity = '1633' then 'MSBNA' when parent_legal_entity in ('0111','0201(G)') Then 'MSCO and MSCS' when parent_legal_entity ='0302(G)' then 'MSIP' when parent_legal_entity ='0517(G)' then 'MSBIL' else 'Others' end as parent_legal_entity, ccc_risk_manager_login, ccc_risk_mgr_name, risk_manager_location, BASEL_III_GROUP_BTI, Case when( CCC_BUSINESS_AREA IN ('CPM','CREDIT', 'MS CVA MNE - FID', 'MS CVA MNE - COMMOD') OR CCC_STRATEGY IN ('MS CVA MPE - DERIVATIVES', 'MS CVA MNE - DERIVATIVES','EQ XVA HEDGING')) then 'CVA' else CCC_DIVISION end as CCC_DIVISION, Case when CCC_PRODUCT_LINE in ('CREL BANK HFI','CRE LENDING SEC/HFS','WAREHOUSE') Then 'WAREHOUSE AND CRE LENDING' When FID1_SENIORITY IN ('AT1', 'SUBT1', 'SUBUT2') Then 'JUNIOR SUBORDINATE' when CCC_PRODUCT_LINE IN ('DISTRESSED TRADING') then 'DISTRESSED TRADING' else CCC_BUSINESS_AREA end as CCC_BUSINESS_AREA, ccc_product_line, product_type_code, sum(case when cob_date ='2018-02-28' then usd_ir_unified_pv01 else 0 end) as usd_pv01_cob, sum(case when cob_date ='2018-02-28' then usd_ir_unified_pv01 else -usd_ir_unified_pv01 end) as usd_pv01_change, sum(case when (a.cob_date ='2018-02-28' and a.vertical_system like'%EQUITY%') then A.USD_PV01SPRD/1000 when cob_date ='2018-02-28' then A.USD_PV01SPRD else 0 end) as usd_pv01sprd_cob, sum(Case when (a.cob_date = '2018-02-28' AND a.vertical_system like'%EQUITY%') then A.USD_PV01SPRD/1000 when (a.cob_date = '2018-02-28') then A.USD_PV01SPRD when (a.cob_date = '2018-01-31' AND a.vertical_system like'%EQUITY%') then -A.USD_PV01SPRD/1000 when (a.cob_date = '2018-01-31') then A.USD_PV01SPRD else 0 end ) as usd_pv01sprd_change, sum(case when cob_date ='2018-02-28' then coalesce(A.USD_PV10_BENCH,a.USD_CREDIT_PV10PCT) else 0 end) as usd_pv10_bench_cob, sum(case when cob_date ='2018-02-28' then coalesce(A.USD_PV10_BENCH,a.USD_CREDIT_PV10PCT) else -coalesce(A.USD_PV10_BENCH,a.USD_CREDIT_PV10PCT) end) as usd_pv10_bench_change, sum(