SELECT A.COB_DATE, CASE WHEN A.CCC_BUSINESS_AREA NOT IN ('LIQUID FLOW RATES','STRUCTURED RATES','FXEM MACRO TRADING','CPM') THEN 'Others' ELSE A.CCC_BUSINESS_AREA END AS BUSINESS_AREA, CASE WHEN A.CURRENCY_OF_MEASURE NOT IN ('GBP','EUR','USD','JPY') THEN 'Other' ELSE A.CURRENCY_OF_MEASURE END AS CURRENCY, SUM(A.USD_IR_UNIFIED_PV01) ::double precision AS USD_IR_UNIFIED_PV01 FROM CDWUSER.U_EXP_MSR A WHERE COB_DATE BETWEEN '2018-01-29' AND '2018-02-28' AND PARENT_LEGAL_ENTITY = '0302(G)' AND CCC_DIVISION = 'FIXED INCOME DIVISION' AND VAR_EXCL_FL <> 'Y' AND BASEL_III_GROUP_BTI = 'TRADING' AND CCC_BUSINESS_AREA NOT IN ('CPM') GROUP BY 1,2,3