SELECT COB_DATE, CCC_BUSINESS_AREA, CCC_PRODUCT_LINE, PRODUCT_TYPE_CODE, DISTRESSED_SECURITY_TYPE, CASE WHEN A.RATING_B IN ('<B','NR') then 'NIG' WHEN A.RATING_B IN ('A','AA','AAA','B','BB','BBB') then 'IG' ELSE A.RATING_B END AS RATING, CASE WHEN A.USD_NET_EXPOSURE >= 0 THEN 'LONG' ELSE 'SHORT' END AS LS_FLAG, sum(USD_NET_EXPOSURE) AS USD_NET_EXPOSURE, PARENT_LEGAL_ENTITY FROM CDWUSER.U_DM_CC A WHERE a.COB_DATE IN ('03/31/2017','04/28/2017','05/31/2017','06/30/2017','07/31/2017','08/31/2017','09/29/2017','10/31/2017','11/30/2017','12/29/2017','01/31/2018','02/28/2018') AND A.CCC_TAPS_COMPANY IN ( '0201','0103','0205','0206','0530','5924') GROUP BY COB_DATE, CCC_BUSINESS_AREA, CCC_PRODUCT_LINE, PRODUCT_TYPE_CODE, DISTRESSED_SECURITY_TYPE, CASE WHEN A.RATING_B IN ('<B','NR') then 'NIG' WHEN A.RATING_B IN ('A','AA','AAA','B','BB','BBB') then 'IG' ELSE A.RATING_B END, CASE WHEN A.USD_NET_EXPOSURE >= 0 THEN 'LONG' ELSE 'SHORT' END, PARENT_LEGAL_ENTITY