SELECT S.COB_DATE, S.SCENARIO_NAME, CASE WHEN S.CCC_TAPS_COMPANY IN ('0201', '0103', '0205', '0206', '0530', '5924') THEN 'MSCO' WHEN S.CCC_TAPS_COMPANY IN ('0111') THEN 'MSCS' ELSE S.CCC_TAPS_COMPANY END AS LE, SUM (S.USD_KAPPA) AS VEGA FROM CDWUSER.U_SURFACE_MSR S WHERE S.COB_DATE IN ('02/28/2018','01/31/2018','12/29/2017') AND S.DIVISION= 'IED' AND S.CCC_BANKING_TRADING = 'TRADING' AND S.SILO_SRC = 'IED' AND S.SCENARIO_NAME IN ('S-20%', 'S-10%', 'S-5%', 'S-2%', 'BASE', 'S+2%', 'S+5%', 'S+10%', 'S+20%') GROUP BY S.COB_DATE, S.SCENARIO_NAME, CASE WHEN S.CCC_TAPS_COMPANY IN ('0201', '0103', '0205', '0206', '0530', '5924') THEN 'MSCO' WHEN S.CCC_TAPS_COMPANY IN ('0111') THEN 'MSCS' ELSE S.CCC_TAPS_COMPANY END