SELECT A.COB_DATE, A.CCC_BUSINESS_AREA, CASE WHEN A.CCC_BANKING_TRADING <> 'TRADING' THEN 'BANKING' ELSE 'TRADING' END AS BANKING_TRADING_FLAG, PARENT_LEGAL_ENTITY, SUM(A.USD_DELTA) AS DELTA FROM CDWUSER.U_DM_EQ A WHERE A.COB_DATE >= '02/01/2017' AND A.COB_DATE <= '02/28/2018' AND A.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND A.CCC_TAPS_COMPANY IN ( '0201','0103','0205','0206','0530','5924') GROUP BY A.COB_DATE, A.CCC_BUSINESS_AREA, A.CCC_HIERARCHY_LEVEL8, CASE WHEN A.CCC_BANKING_TRADING <> 'TRADING' THEN 'BANKING' ELSE 'TRADING' END , PARENT_LEGAL_ENTITY