SELECT a.COB_DATE, PARENT_LEGAL_ENTITY, a.MRD_RATING, SUM (CASE WHEN a.PRODUCT_TYPE_CODE IN ('ADR', 'STOCK', 'SWAP', 'WARRNT', 'ETF', 'OPTION') then a.USD_DELTA ELSE a.USD_EXPOSURE END) AS NET_EXPOSURE FROM CDWUSER.U_EXP_MSR A WHERE a.COB_DATE IN ('03/31/2017','04/28/2017','05/31/2017','06/30/2017','07/31/2017','08/31/2017','09/29/2017','10/31/2017','11/30/2017','12/29/2017','01/31/2018','02/28/2018') AND a.CCC_PRODUCT_LINE = 'DISTRESSED TRADING' AND PRODUCT_TYPE_CODE NOT IN ('GVTBOND', 'ETF', 'OPTION', 'CRDINDEX') AND A.CCC_TAPS_COMPANY IN ( '0201','0103','0205','0206','0530','5924') GROUP BY a.COB_DATE, PARENT_LEGAL_ENTITY, a.MRD_RATING