SELECT a.COB_DATE ,a.PARENT_LEGAL_ENTITY , a.ctpy_netting_group_code , a.POSITION_CREDIT_PARTY_DARWIN_NAME , 0 as rank ,sum(a.USD_IR_UNIFIED_PV01) as USD_IR_UNIFIED_PV01 FROM OERUSER.U_EXP_MSR a WHERE a.COB_DATE in ('2018-02-28') AND a.PARENT_LEGAL_ENTITY in ('0111') and a.POSITION_CREDIT_PARTY_DARWIN_NAME in( 'CHICAGO MERCANTILE EXCHANGE INC.', 'LCH.CLEARNET LIMITED','LCH LIMITED', 'MORGAN STANLEY', 'MORGAN STANLEY & CO. LLC', 'MORGAN STANLEY & CO. INTERNATIONAL PLC', 'MORGAN STANLEY MUFG SECURITIES CO., LTD.', 'N/A' ) GROUP BY a.COB_DATE ,a.PARENT_LEGAL_ENTITY ,a.ctpy_netting_group_code ,a.POSITION_CREDIT_PARTY_DARWIN_NAME UNION SELECT a.COB_DATE ,a.PARENT_LEGAL_ENTITY , a.ctpy_netting_group_code ,a.POSITION_CREDIT_PARTY_DARWIN_NAME ,rank() OVER (ORDER BY abs(sum(a.USD_IR_UNIFIED_PV01)) DESC) AS rank ,sum(a.USD_IR_UNIFIED_PV01) as USD_IR_UNIFIED_PV01 FROM OERUSER.U_EXP_MSR a WHERE a.COB_DATE in ('2018-02-28') AND a.PARENT_LEGAL_ENTITY in ('0111') and a.POSITION_CREDIT_PARTY_DARWIN_NAME NOT in( 'CHICAGO MERCANTILE EXCHANGE INC.', 'LCH.CLEARNET LIMITED','LCH LIMITED', 'MORGAN STANLEY', 'MORGAN STANLEY & CO. LLC', 'MORGAN STANLEY & CO. INTERNATIONAL PLC', 'MORGAN STANLEY MUFG SECURITIES CO., LTD.', 'N/A' ) GROUP BY a.COB_DATE ,a.PARENT_LEGAL_ENTITY ,a.ctpy_netting_group_code ,a.POSITION_CREDIT_PARTY_DARWIN_NAME