SELECT CASE WHEN A.WM_BANKING_PRODUCT_GROUP = 'AFS' OR A.BOOK IN ('PBCMB') OR (A.BOOK = 'GWM_INVESTPORT_TRUST' AND A.ACCOUNT IN ('0710PT251', '0710PT3A9')) THEN 'AFS' WHEN account IN ('072000GC0') THEN 'HTM' ELSE a.WM_BANKING_PRODUCT_GROUP END AS WM_BANKING_PRODUCT_GROUP, CASE WHEN account IN ('072000GC0','071000B85','07100AS69','072000GC0','07200B6M5','07100ATD3', '07200B6K9','07100ATF8','07200B6H6','07200B6J2') THEN 'HTM Agency' WHEN ACCOUNT IN ('072000F72', '07200B6G8') THEN 'HTM Govt Bonds' WHEN A.BOOK = 'GWM_INVESTPORT_TRUST' AND A.ACCOUNT IN ('0710PT251', '0710PT3A9') THEN 'AFS AGN FIXED' WHEN A.BOOK IN ('PBCMB') THEN 'AFS Credit Fixed' ELSE a.WM_BANKING_PRODUCT_SUB_GROUP END AS WM_BANKING_PRODUCT_SUB_GROUP, SUM (cast (A.USD_EXPOSURE as numeric(15,5))) AS NE FROM CDWUSER.U_DM_WM a WHERE a.COB_DATE IN ('2018-02-28') and (a.WM_BANKING_PRODUCT_SUB_GROUP IN ('AFS AGN Fixed', 'AFS AGN Float', 'AFS Credit Fixed', 'AFS Credit Float', 'AFS Govt Bonds', 'HTM Agency', 'HTM Govt Bonds') OR account IN ('072000GC0') OR A.BOOK ='PBCMB' OR (A.BOOK = 'GWM_INVESTPORT_TRUST' AND A.ACCOUNT IN ('0710PT251', '0710PT3A9')) OR A.ACCOUNT IN ( '072000F72','07200B6G8','071000B85','07100AS69','072000GC0', '07200B6M5', '07100ATD3', '07200B6K9', '07100ATF8', '07200B6H6', '07200B6J2' ) ) GROUP BY CASE WHEN A.WM_BANKING_PRODUCT_GROUP = 'AFS' OR A.BOOK IN ('PBCMB') OR (A.BOOK = 'GWM_INVESTPORT_TRUST' AND A.ACCOUNT IN ('0710PT251', '0710PT3A9')) THEN 'AFS' WHEN account IN ('072000GC0') THEN 'HTM' ELSE a.WM_BANKING_PRODUCT_GROUP END, CASE WHEN account IN ('072000GC0','071000B85','07100AS69','072000GC0','07200B6M5','07100ATD3', '07200B6K9','07100ATF8','07200B6H6','07200B6J2') THEN 'HTM Agency' WHEN ACCOUNT IN ('072000F72', '07200B6G8') THEN 'HTM Govt Bonds' WHEN A.BOOK = 'GWM_INVESTPORT_TRUST' AND A.ACCOUNT IN ('0710PT251', '0710PT3A9') THEN 'AFS AGN FIXED' WHEN A.BOOK IN ('PBCMB') THEN 'AFS Credit Fixed' ELSE a.WM_BANKING_PRODUCT_SUB_GROUP END