EN 'BRL' WHEN a.CURRENCY_OF_MEASURE ='KRX' THEN 'KRW' WHEN a.CURRENCY_OF_MEASURE in ('RUB','RU1','RBX') THEN 'RUB' ELSE a.CURRENCY_OF_MEASURE END AS CURRENCY_BUCKET , ctpy_netting_group_code , sum(a.USD_IR_UNIFIED_PV01) as USD_IR_UNIFIED_PV01 , 0 as rank , 0 as rank_currency FROM OERUSER.U_EXP_MSR a WHERE a.COB_DATE in ('2018-02-28','2018-02-27') AND a.PARENT_LEGAL_ENTITY in ('0201(G)') and a.ctpy_netting_group_code in ('2454343', '2523339','2375403') GROUP BY a.COB_DATE , a.PARENT_LEGAL_ENTITY , a.POSITION_CREDIT_PARTY_DARWIN_NAME , a.CURRENCY_OF_MEASURE , ctpy_netting_group_code