SELECT a.COB_DATE , a.PARENT_LEGAL_ENTITY , CASE WHEN a.CURRENCY_OF_MEASURE ='CNH' THEN 'CNY' WHEN a.CURRENCY_OF_MEASURE IN ('BRX','BR1','BRL') THEN 'BRL' WHEN a.CURRENCY_OF_MEASURE ='KRX' THEN 'KRW' WHEN a.CURRENCY_OF_MEASURE in ('RUB','RU1','RBX') THEN 'RUB' ELSE a.CURRENCY_OF_MEASURE END AS CURRENCY_BUCKET ,SUM(CASE WHEN A.CURRENCY_OF_MEASURE IN ('UBD','USD') THEN 0 ELSE COALESCE(A.USD_FX,0) END) AS FX_DELTA FROM CDWUSER.U_EXP_MSR a WHERE a.COB_DATE in ('2018-02-28','2018-02-27') AND a.PARENT_LEGAL_ENTITY in ('0201(G)') group by a.COB_DATE , a.PARENT_LEGAL_ENTITY , CASE WHEN a.CURRENCY_OF_MEASURE ='CNH' THEN 'CNY' WHEN a.CURRENCY_OF_MEASURE IN ('BRX','BR1','BRL') THEN 'BRL' WHEN a.CURRENCY_OF_MEASURE ='KRX' THEN 'KRW' WHEN a.CURRENCY_OF_MEASURE in ('RUB','RU1','RBX') THEN 'RUB' ELSE a.CURRENCY_OF_MEASURE END