SELECT 'UK GROUP' as CUT, COB_DATE, sum(DELTA) as DELTA, sum(Abs_Delta) as GROSS_DELTA FROM (SELECT d.COB_DATE, d.ISSUE_ID_DECOMP, sum(coalesce(d.USD_EQ_DELTA_DECOMP,0)) as Delta, abs(sum(coalesce(d.USD_EQ_DELTA_DECOMP,0))) as Abs_Delta FROM CDWUSER.U_DECOMP_MSR d WHERE d.COB_DATE = '2018-02-28' AND d.CCC_BANKING_TRADING = 'TRADING' AND d.SILO_SRC = 'IED' AND d.DIVISION = 'IED' AND CCC_TAPS_COMPANY in ('7800','4068','8962','8961','8959','8941','8790','8789','8772','8757','8627','8564','8537','8524','8441','8292','8290','8284','8275','8253','8237','8179','8174','7716','7705','7704','7458','7435','7416','7281','7280','7043','7016','6899','6893','6838','6837','6590','6589','6384','6383','6376','6374','6367','6325','6316','6262','6158','6157','6120','6114','6036','5869','5856','5656','5614','5357','5310','5274','5254','5181','5180','5148','5121','5104','5103','4884','4880','4876','4863','4859','4858','4857','4590','4564','4562','4545','4543','4536','4391','4267','4241','4092','4086','4067','4044','4043','1718','1709','1498','1480','1472','1438','1433','1344','1322','1317','1314','1313','1311','1308','0993','0856','0853','0726','0721','0715','0713','0621','0620','0517','0347','0342','0328','0319','0314','0313','0302') AND (d.CASH_ISSUE_TYPE <>'COMM' OR d.CASH_ISSUE_TYPE IS NULL) group by d.COB_DATE, d.ISSUE_ID_DECOMP) x Group By COB_DATE UNION SELECT 'MSIP' as CUT, COB_DATE, sum(DELTA) as DELTA, sum(Abs_Delta) as GROSS_DELTA FROM (SELECT d.COB_DATE, d.ISSUE_ID_DECOMP, sum(coalesce(d.USD_EQ_DELTA_DECOMP,0)) as Delta, abs(sum(coalesce(d.USD_EQ_DELTA_DECOMP,0))) as Abs_Delta FROM CDWUSER.U_DECOMP_MSR d WHERE d.COB_DATE = '2018-02-28' AND d.CCC_BANKING_TRADING = 'TRADING' AND d.SILO_SRC = 'IED' AND d.DIVISION = 'IED' AND CCC_TAPS_COMPANY in ('0302') AND (d.CASH_ISSUE_TYPE <>'COMM' OR d.CASH_ISSUE_TYPE IS NULL) group by d.COB_DATE, d.ISSUE_ID_DECOMP) x Group By COB_DATE UNION SELECT 'EMEA' as CUT, COB_DATE, sum(DELTA) as DELTA, sum(Abs_Delta) as GROSS_DELTA FROM (SELECT d.COB_DATE, d.ISSUE_ID_DECOMP, sum(coalesce(d.USD_EQ_DELTA_DECOMP,0)) as Delta, abs(sum(coalesce(d.USD_EQ_DELTA_DECOMP,0))) as Abs_Delta FROM CDWUSER.U_DECOMP_MSR d WHERE d.COB_DATE = '2018-02-28' AND d.CCC_BANKING_TRADING = 'TRADING' AND d.SILO_SRC = 'IED' AND d.DIVISION = 'IED' AND d.CCC_PL_REPORTING_REGION = 'EMEA' AND (d.CASH_ISSUE_TYPE <>'COMM' OR d.CASH_ISSUE_TYPE IS NULL) group by d.COB_DATE, d.ISSUE_ID_DECOMP) x Group By COB_DATE UNION SELECT 'FIRM' as CUT, COB_DATE, sum(DELTA) as DELTA, sum(Abs_Delta) as GROSS_DELTA FROM (SELECT d.COB_DATE, d.ISSUE_ID_DECOMP, sum(coalesce(d.USD_EQ_DELTA_DECOMP,0)) as Delta, abs(sum(coalesce(d.USD_EQ_DELTA_DECOMP,0))) as Abs_Delta FROM CDWUSER.U_DECOMP_MSR d WHERE d.COB_DATE = '2018-02-28' AND d.CCC_BANKING_TRADING = 'TRADING' AND d.SILO_SRC = 'IED' AND d.DIVISION = 'IED' AND (d.CASH_ISSUE_TYPE <>'COMM' OR d.CASH_ISSUE_TYPE IS NULL) group by d.COB_DATE, d.ISSUE_ID_DECOMP) x Group By COB_DATE