SELECT     a.COB_DATE,     SUM (a.USD_PV10_BENCH) AS BPV10,     a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME,      SUM (a.usd_exposure) AS NE,     a.ISSUER_COUNTRY_CODE,      CASE WHEN a.CR_SPREAD_MARK_5Y<0 then 0 else a.CR_SPREAD_MARK_5Y END as CR_SPREAD_MARK_5Y,      a.credit_spread FROM CDWUSER.U_CR_MSR A where COB_DATE='2018-02-28' AND A.CCC_DIVISION='INSTITUTIONAL EQUITY DIVISION'     AND a.CR_SPREAD_MARK_5Y IS NOT NULL AND     A.REFERENCE_INDEX_ENTITY_NAME NOT LIKE '%CDX.NA.IG%' AND A.REFERENCE_INDEX_ENTITY_NAME NOT LIKE '%CDX.NA.HY%' AND     A.REFERENCE_INDEX_ENTITY_NAME NOT LIKE '%ITRAXX EUROPE CROSSOVER%' AND     A.REFERENCE_INDEX_ENTITY_NAME NOT LIKE '%ITRAXX EUROPE SERIES%'     and a.ISSUER_COUNTRY_CODE='BRA' and a.CCC_BANKING_TRADING_LOCALREG='TRADING' AND A.VAR_EXCL_FL='N' AND CCC_BUSINESS_AREA NOT LIKE '%CPM%'  GROUP BY     a.COB_DATE,     a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME,      a.ISSUER_COUNTRY_CODE,     a.CR_SPREAD_MARK_5Y,     a.credit_spread