SELECT SUM (USD_IR_UNIFIED_PV01) AS IRPV01, TERM_BUCKET, COB_DATE, CCC_BUSINESS_AREA, CASE WHEN (VERTICAL_SYSTEM LIKE '%EQUITY%' AND PRODUCT_TYPE_CODE NOT IN ('GVTBOND' ,'RATEFUT')) THEN 'OTHER EQ' ELSE 'OTHER FID' END AS CATEGORY, CASE WHEN PRODUCT_TYPE_CODE IN ('GVTBOND' ) THEN ' Government Bonds' WHEN PRODUCT_TYPE_CODE LIKE '%RATEFUT%' THEN 'Rate Futures' ELSE 'OTHERS' END AS PRODUCT_TYPE_CODE FROM  CDWUSER.U_EXP_MSR V where COB_DATE='2018-02-28' AND v.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND NOT PRODUCT_DESCRIPTION LIKE '%BLFT%' AND CURRENCY_OF_MEASURE LIKE '%BR%' AND (USD_IR_UNIFIED_PV01 <> 0) AND CCC_BUSINESS_AREA NOT LIKE '%CPM%' GROUP BY COB_DATE, TERM_BUCKET, CCC_BUSINESS_AREA, CASE WHEN (VERTICAL_SYSTEM LIKE '%EQUITY%' AND PRODUCT_TYPE_CODE NOT IN ('GVTBOND' ,'RATEFUT')) THEN 'OTHER EQ' ELSE 'OTHER FID' END, CASE WHEN PRODUCT_TYPE_CODE IN ('GVTBOND' ) THEN ' Government Bonds' WHEN PRODUCT_TYPE_CODE LIKE '%RATEFUT%' THEN 'Rate Futures' ELSE 'OTHERS' END