Select a.COB_DATE, case when a.TICKER_DECOMP || '.' || a.EXCHANGE_DECOMP in ('abx.to','bns.to', 'td.to', 'ry.to','rio.l') then a.TICKER_DECOMP || '.n' else a.TICKER_DECOMP || '.' || a.EXCHANGE_DECOMP end as Decomp_Symbol , case when sum(coalesce(a.USD_EQ_DELTA_DECOMP,0)+coalesce(a.USD_CM_DELTA_DECOMP,0))<0 then 'Short' else 'Long' end as Long_Short, SUM (CASE WHEN COB_DATE = '2018-02-28' THEN coalesce(a.USD_EQ_DELTA_DECOMP,0)+coalesce(a.USD_CM_DELTA_DECOMP,0) ELSE 0 END) AS DELTA_USD_COB, SUM (CASE WHEN COB_DATE = '2018-02-27' THEN coalesce(a.USD_EQ_DELTA_DECOMP,0)+coalesce(a.USD_CM_DELTA_DECOMP,0) ELSE 0 END) AS DELTA_USD_COMP, SUM (CASE WHEN COB_DATE = '2018-02-28' THEN coalesce(a.USD_EQ_DELTA_DECOMP,0)+coalesce(a.USD_CM_DELTA_DECOMP,0) ELSE - (coalesce(a.USD_EQ_DELTA_DECOMP,0)+coalesce(a.USD_CM_DELTA_DECOMP,0)) END) AS DELTA_CHANGE from cdwuser.U_DECOMP_MSR a where a.COB_DATE in ('2018-02-28', '2018-02-27') and a.DIVISION = 'IED' and a.SILO_SRC = 'IED' and a.CCC_BANKING_TRADING ='TRADING' and a.CCC_PL_REPORTING_REGION = 'AMERICAS' AND a.CCC_STRATEGY='SINGLE NAME'  group by a.COB_DATE, case when a.TICKER_DECOMP || '.' || a.EXCHANGE_DECOMP in ('abx.to','bns.to', 'td.to', 'ry.to','rio.l') then a.TICKER_DECOMP || '.n' else a.TICKER_DECOMP || '.' || a.EXCHANGE_DECOMP end having abs(sum(coalesce(a.USD_EQ_DELTA_DECOMP,0)+coalesce(a.USD_CM_DELTA_DECOMP,0))) > 0