SELECT A.CCC_DIVISION,   CASE    WHEN A.PRODUCT_TYPE_CODE IN (     'GVTBOND'     )    THEN 'Government Bonds'   WHEN A.PRODUCT_TYPE_CODE LIKE '%RATEFUT%'    THEN 'Rate Futures'   ELSE 'OTHERS'   END AS PRODUCT_TYPE_CODE, CASE    WHEN (     a.VERTICAL_SYSTEM LIKE '%EQUITY%'     AND a.PRODUCT_TYPE_CODE NOT IN (      'GVTBOND',      'RATEFUT'      )     )    THEN 'OTHER EQ'   ELSE 'OTHER FID'   END AS CATEGORY, SUM(A.USD_FX) AS Delta  FROM CDWUSER.U_FX_MSR A where COB_DATE='2018-02-28' AND A.CURRENCY_OF_MEASURE IN ('BRX','BR1','BRL') AND  (USD_FX<>0 ) AND A.VERTICAL_SYSTEM NOT IN ('PIPELINE_NY') AND A.VAR_EXCL_FL='N' AND A.CVA_FL='N' AND CCC_BUSINESS_AREA NOT LIKE '%CPM%' AND (A.CCC_DIVISION='INSTITUTIONAL EQUITY DIVISION' AND A.CCC_BANKING_TRADING_LOCALREG='TRADING') group by a.CCC_DIVISION, CASE    WHEN A.PRODUCT_TYPE_CODE IN (     'GVTBOND'     )    THEN 'Government Bonds'   WHEN A.PRODUCT_TYPE_CODE LIKE '%RATEFUT%'    THEN 'Rate Futures'   ELSE 'OTHERS'   END, CASE    WHEN (     a.VERTICAL_SYSTEM LIKE '%EQUITY%'     AND a.PRODUCT_TYPE_CODE NOT IN (      'GVTBOND',      'RATEFUT'      )     )    THEN 'OTHER EQ'   ELSE 'OTHER FID'   END