SELECT A.CCC_DIVISION, term_bucket, CASE WHEN A.FID1_INDUSTRY_NAME_LEVEL1='SOVEREIGN' THEN 'SOV' ELSE 'NON SOV' END AS SOV_FLAG, SUM(COALESCE(A.USD_EXPOSURE,0)) AS NET_EXP FROM CDWUSER.U_EXP_MSR A where COB_DATE='2018-02-28' AND (UPPER (a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME) NOT LIKE '%MORGAN STANLEY%') AND A.CCC_DIVISION IN ('INSTITUTIONAL EQUITY DIVISION') AND A.ISSUER_COUNTRY_CODE='BRA' AND CCC_BUSINESS_AREA NOT LIKE '%CPM%' AND A.CCC_BANKING_TRADING_LOCALREG='TRADING' AND A.FID1_INDUSTRY_NAME_LEVEL1 <>'UNDEFINED' GROUP BY CASE WHEN A.FID1_INDUSTRY_NAME_LEVEL1='SOVEREIGN' THEN 'SOV' ELSE 'NON SOV' END, A.CCC_DIVISION, term_bucket         ORDER BY A.CCC_DIVISION