SELECT a.COB_DATE, CASE WHEN A.FID1_INDUSTRY_NAME_LEVEL1='SOVEREIGN' THEN 'SOV' ELSE 'NON SOV' END AS SOV_FL, CASE WHEN A.CURRENCY_OF_MEASURE IN ('CNY','CNH') then 'EM' else 'MAJOR_EM' END AS CCY_TYPE, SUM(A.USD_EXPOSURE) AS NET_EXP FROM CDWUSER.U_EXP_MSR A where COB_DATE='2018-02-28' AND A.CCC_DIVISION='INSTITUTIONAL EQUITY DIVISION' AND A.ISSUER_COUNTRY_CODE='BRA' AND CCC_BUSINESS_AREA NOT LIKE '%CPM%'     AND A.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME <>'MORGAN STANLEY'  AND a.VAR_EXCL_FL = 'N'   AND A.CCC_BANKING_TRADING_LOCALREG='TRADING' GROUP BY a.COB_DATE, CASE WHEN A.FID1_INDUSTRY_NAME_LEVEL1='SOVEREIGN' THEN 'SOV' ELSE 'NON SOV' END, CASE WHEN A.CURRENCY_OF_MEASURE IN ('CNY','CNH') then 'EM' else 'MAJOR_EM' END