SELECT a.COB_DATE, a.VERTICAL_SYSTEM ,A.TERM_BUCKET ,SUM(USD_IR_UNIFIED_PV01) AS PV01 ,a.PRODUCT_TYPE_CODE, case when (a.VERTICAL_SYSTEM like '%EQUITY%'  and a.PRODUCT_TYPE_CODE not in ('GVTBOND','RATEFUT')) THEN 'OTHER EQ' ELSE 'OTHER FID' END AS CATEGORY ,CASE WHEN A.PRODUCT_TYPE_CODE in ('GVTBOND') THEN ' Government Bonds' WHEN A.PRODUCT_TYPE_CODE LIKE '%RATEFUT%' THEN 'Rate Futures' else 'OTHERS' END AS PRODUCT_TYPE_CODE FROM CDWUSER.U_exp_msr A where COB_DATE='2018-02-28' AND A.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND CURRENCY_OF_MEASURE LIKE '%BR%' AND CCC_BUSINESS_AREA NOT LIKE '%CPM%' AND (USD_IR_UNIFIED_PV01 <> 0) AND NOT a.PRODUCT_DESCRIPTION LIKE '%BLFT%' GROUP BY a.COB_DATE ,a.PRODUCT_TYPE_CODE, A.TERM_BUCKET ,CASE WHEN A.PRODUCT_TYPE_CODE in ('GVTBOND')   THEN ' Government Bonds' WHEN A.PRODUCT_TYPE_CODE LIKE '%RATEFUT%' THEN 'Rate Futures' else 'OTHERS' END, a.vertical_system, case when (a.VERTICAL_SYSTEM like '%EQUITY%'  and a.PRODUCT_TYPE_CODE not in ('GVTBOND','RATEFUT')) THEN 'OTHER EQ' ELSE 'OTHER FID' END