SELECT a.COB_DATE, a.UNDERLIER_TICK, a.TRADE_SIZE, round(a.UNDERLIER_PRICE,2) as PRICE, sum(round(a.USD_DELTA,2)) as DELTA, a.EXECUTIVE_MODEL, a.CCC_STRATEGY_DETAILS, a.CCC_STRATEGY  FROM cdwuser.U_IED_VIEW a  WHERE a.COB_DATE > ('2018-01-29') AND a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND a.CCC_PRODUCT_LINE = 'CORE PRIMARY' AND a.CCC_PL_REPORTING_REGION = 'AMERICAS' AND a.CCC_BANKING_TRADING = 'TRADING'  AND a.USD_DELTA IS NOT NULL  GROUP BY a.COB_DATE, a.UNDERLIER_TICK, a.TRADE_SIZE, a.UNDERLIER_PRICE, a.USD_DELTA, a.EXECUTIVE_MODEL, a.CCC_STRATEGY_DETAILS, a.CCC_STRATEGY