SELECT COB_DATE, DIVISION_GROUP, BREAKOUT_LINE, IS_ABS, DIVISION_CATEGORY, CASE WHEN DIVISION_GROUP IN ('ISG CORE') THEN DIVISION_GROUP WHEN DIVISION_GROUP IN ('ISG NON CORE') THEN DIVISION_GROUP ELSE 'OTHERS' END AS DIVISION_GROUP2, SUM(CAST(EXPOSURE AS NUMERIC (15,5))) AS EXPOSURE FROM ( SELECT A.COB_DATE, A.DIVISION_GROUP, BREAKOUT_LINE, CASE WHEN ( BREAKOUT_LINE = 'ABS - Agency' AND DIVISION_GROUP = 'ISG CORE' ) OR BREAKOUT_LINE = 'ABS - Non-Agency' THEN 'ABS' ELSE 'NON_ABS' END AS IS_ABS, CASE WHEN DIVISION_GROUP IN ('ISG NON CORE', 'TREASURY', 'GWMG', 'OTHER ISG') THEN DIVISION_GROUP WHEN CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND DIVISION_GROUP = 'ISG CORE' THEN 'EQUITY' WHEN ( CCC_DIVISION IN ('COMMODITIES') OR CCC_BUSINESS_AREA IN ('COMMODITIES') ) AND DIVISION_GROUP = 'ISG CORE' THEN 'COMM' WHEN CCC_DIVISION NOT IN ('INSTITUTIONAL EQUITY DIVISION', 'COMMODITIES', 'FIXED INCOME DIVISION') AND DIVISION_GROUP IN ('ISG CORE', 'ISG NON CORE') THEN 'OTHER ISG' WHEN DIVISION_GROUP = 'ISG CORE' AND CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA NOT IN ('COMMODITIES') THEN 'FID' ELSE 'NON ISG' END AS DIVISION_CATEGORY, SUM(CAST(USD_EXPOSURE AS NUMERIC (15,5)))/1000000 AS EXPOSURE FROM CDWUSER.U_DM_FIRMWIDE A WHERE COB_DATE >= '07/01/2016' AND COB_DATE <= '02/28/2018' AND VERTICAL_SYSTEM <> 'PIPELINE_NY' AND VAR_EXCL_FL <> 'Y' AND ISSUER_COUNTRY_TIER = '1' AND CREDIT_TYPE_CD = 'SECURITIZED T1T2' AND PRODUCT_TYPE_CODE NOT IN ('AGN') GROUP BY A.COB_DATE, A.DIVISION_GROUP, BREAKOUT_LINE, CASE WHEN ( BREAKOUT_LINE = 'ABS - Agency' AND DIVISION_GROUP = 'ISG CORE' ) OR BREAKOUT_LINE = 'ABS - Non-Agency' THEN 'ABS' ELSE 'NON_ABS' END, CASE WHEN DIVISION_GROUP IN ('ISG NON CORE', 'TREASURY', 'GWMG', 'OTHER ISG') THEN DIVISION_GROUP WHEN CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND DIVISION_GROUP = 'ISG CORE' THEN 'EQUITY' WHEN ( CCC_DIVISION IN ('COMMODITIES') OR CCC_BUSINESS_AREA IN ('COMMODITIES') ) AND DIVISION_GROUP = 'ISG CORE' THEN 'COMM' WHEN CCC_DIVISION NOT IN ('INSTITUTIONAL EQUITY DIVISION', 'COMMODITIES', 'FIXED INCOME DIVISION') AND DIVISION_GROUP IN ('ISG CORE', 'ISG NON CORE') THEN 'OTHER ISG' WHEN DIVISION_GROUP = 'ISG CORE' AND CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA NOT IN ('COMMODITIES') THEN 'FID' ELSE 'NON ISG' END ) sub_qry WHERE BREAKOUT_LINE IN ('ABS - Agency', 'ABS - Non-Agency', 'RMBS', 'CMBS Agency', 'Agency', 'CMBS Non-Agency') AND DIVISION_GROUP NOT IN ('OTHER') GROUP BY COB_DATE, DIVISION_GROUP, BREAKOUT_LINE, IS_ABS, DIVISION_CATEGORY, CASE WHEN DIVISION_GROUP IN ('ISG CORE') THEN DIVISION_GROUP WHEN DIVISION_GROUP IN ('ISG NON CORE') THEN DIVISION_GROUP ELSE 'OTHERS' END