SELECT COB_DATE, CASE WHEN DIVISION_GROUP IN ('ISG NON CORE', 'TREASURY', 'GWMG', 'OTHER ISG')THEN DIVISION_GROUP WHEN CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND DIVISION_GROUP = 'ISG CORE' THEN 'EQUITY' WHEN ( CCC_DIVISION = 'COMMODITIES' OR CCC_BUSINESS_AREA = 'COMMODITIES' ) AND DIVISION_GROUP = 'ISG CORE' THEN 'COMM' WHEN CCC_DIVISION NOT IN ('INSTITUTIONAL EQUITY DIVISION', 'COMMODITIES', 'FIXED INCOME DIVISION') AND DIVISION_GROUP IN ('ISG CORE', 'ISG NON CORE') THEN 'OTHER ISG' WHEN DIVISION_GROUP = 'ISG CORE' AND CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA NOT IN ('COMMODITIES') THEN 'FID' ELSE 'NON ISG' END AS DIVISION_CATEGORY, CASE WHEN CCC_BUSINESS_AREA = 'CREDIT-CORPORATES' AND COALESCE(CCC_PRODUCT_LINE,'') NOT IN ('PRIMARY - BONDS','PRIMARY - IG BONDS','PRIMARY - NIG BONDS') AND COALESCE(FID1_SENIORITY,'') <> 'AT1' THEN 'CREDIT_CORP' WHEN CCC_BUSINESS_AREA = 'LENDING' OR CCC_PRODUCT_LINE IN ('PRIMARY - BONDS','PRIMARY - IG BONDS','PRIMARY - NIG BONDS') THEN 'LENDING' WHEN CCC_BUSINESS_AREA IN ('CREDIT DERIVATIVE PROD', 'DSP - CREDIT') THEN 'DSP - CREDIT' WHEN CCC_BUSINESS_AREA IN ('MUNICIPAL SECURITIES') THEN CCC_BUSINESS_AREA WHEN CCC_BUSINESS_AREA IN ('FXEM MACRO TRADING', 'EM CREDIT TRADING') THEN 'FXEM' WHEN CCC_BUSINESS_AREA IN ('LIQUID FLOW RATES', 'STRUCTURED RATES') THEN 'IR_RATES' ELSE 'OTHER' END AS BREAKDOWN, CASE WHEN CCC_DIVISION = 'FIXED INCOME DIVISION' AND DIVISION_GROUP = 'ISG CORE' AND CCC_BUSINESS_AREA NOT IN ('COMMODITIES') THEN 'FID' ELSE 'NON_FID' END AS IS_FID, SUM(COALESCE(USD_BPV10,0)) AS BPV10 FROM CDWUSER.U_DM_FIRMWIDE WHERE COB_DATE IN ('02/28/2018', '01/31/2018') AND USD_BPV10 IS NOT NULL AND CCC_BUSINESS_AREA NOT LIKE 'CPM%' AND CCC_BUSINESS_AREA <> 'DERIVS FUNDING' AND COALESCE(CCC_PRODUCT_LINE,'') <> 'DISTRESSED TRADING' GROUP BY COB_DATE, CASE WHEN DIVISION_GROUP IN ('ISG NON CORE', 'TREASURY', 'GWMG', 'OTHER ISG')THEN DIVISION_GROUP WHEN CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND DIVISION_GROUP = 'ISG CORE' THEN 'EQUITY' WHEN ( CCC_DIVISION = 'COMMODITIES' OR CCC_BUSINESS_AREA = 'COMMODITIES' ) AND DIVISION_GROUP = 'ISG CORE' THEN 'COMM' WHEN CCC_DIVISION NOT IN ('INSTITUTIONAL EQUITY DIVISION', 'COMMODITIES', 'FIXED INCOME DIVISION') AND DIVISION_GROUP IN ('ISG CORE', 'ISG NON CORE') THEN 'OTHER ISG' WHEN DIVISION_GROUP = 'ISG CORE' AND CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA NOT IN ('COMMODITIES') THEN 'FID' ELSE 'NON ISG' END, CASE WHEN CCC_BUSINESS_AREA = 'CREDIT-CORPORATES' AND COALESCE(CCC_PRODUCT_LINE,'') NOT IN ('PRIMARY - BONDS','PRIMARY - IG BONDS','PRIMARY - NIG BONDS') AND COALESCE(FID1_SENIORITY,'') <> 'AT1' THEN 'CREDIT_CORP' WHEN CCC_BUSINESS_AREA = 'LENDING' OR CCC_PRODUCT_LINE IN ('PRIMARY - BONDS','PRIMARY - IG BONDS','PRIMARY - NIG BONDS') THEN 'LENDING' WHEN CCC_BUSINESS_AREA IN ('CREDIT DERIVATIVE PROD', 'DSP - CREDIT') THEN 'DSP - CREDIT' WHEN CCC_BUSINESS_AREA IN ('MUNICIPAL SECURITIES') THEN CCC_BUSINESS_AREA WHEN CCC_BUSINESS_AREA IN ('FXEM MACRO TRADING', 'EM CREDIT TRADING') THEN 'FXEM' WHEN CCC_BUSINESS_AREA IN ('LIQUID FLOW RATES', 'STRUCTURED RATES') THEN 'IR_RATES' ELSE 'OTHER' END, CASE WHEN CCC_DIVISION = 'FIXED INCOME DIVISION' AND DIVISION_GROUP = 'ISG CORE' AND CCC_BUSINESS_AREA NOT IN ('COMMODITIES') THEN 'FID' ELSE 'NON_FID' END