SELECT a.COB_DATE,  a.CCC_RISK_MANAGER_LOGIN, SUM(a.USD_DELTA) AS DELTA, SUM(a.USD_EQ_GAMMA) AS GAMMA, SUM(a.USD_VEGA) AS VEGA,  SUM(a.USD_TA_VEGA) AS TAVEGA, SUM(a.USD_THETA_GAMMA) AS THETAGAMMA, SUM(a.SLIDE_MIN_30_USD+a.USD_DELTA*.3) AS D30, SUM(a.SLIDE_MIN_20_USD+a.USD_DELTA*.2) AS D20, SUM(a.SLIDE_MIN_10_USD+a.USD_DELTA*.1) AS D10, SUM(a.SLIDE_MIN_05_USD+a.USD_DELTA*.05) AS D05, SUM(a.SLIDE_MIN_02_USD+a.USD_DELTA*.02) AS D02, SUM(a.SLIDE_PLS_02_USD-a.USD_DELTA*.02) AS P02, SUM(a.SLIDE_PLS_05_USD-a.USD_DELTA*.05) AS P05, SUM(a.SLIDE_PLS_10_USD-a.USD_DELTA*.1) AS P10, SUM(a.SLIDE_PLS_20_USD-a.USD_DELTA*.2) AS P20 FROM   CDWUSER.U_IED_VIEW a  WHERE a.COB_DATE IN ('2018-02-28','2018-02-27') AND A.CCC_PL_REPORTING_REGION in ('AMERICAS')  AND a.ccc_division IN ('INSTITUTIONAL EQUITY DIVISION') AND a.CCC_BANKING_TRADING = 'TRADING'  GROUP  BY a.cob_date, a.ccc_risk_manager_login UNION ALL SELECT a.COB_DATE, a.CCC_RISK_MANAGER_LOGIN, 0 AS DELTA, SUM(a.USD_CM_GAMMA) AS GAMMA, SUM(a.USD_CM_KAPPA) AS VEGA,  0 AS TAVEGA, 0 AS THETAGAMMA, 0 AS D30, 0 AS D20, 0 AS D10, 0 AS D05, 0 AS D02, 0 AS P02, 0 AS P05, 0 AS P10, 0 AS P20 FROM CDWUSER.U_CM_MSR a WHERE a.COB_DATE IN ('2018-02-28','2018-02-27') AND a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND a.CCC_PL_REPORTING_REGION = 'AMERICAS' AND a.CCC_BANKING_TRADING = 'TRADING' GROUP BY a.COB_DATE, a.CCC_RISK_MANAGER_LOGIN