SELECT COB_DATE, ISSUER_COUNTRY_CODE_DECOMP, ccc_business_area ,sum(coalesce(USD_EQ_DELTA_DECOMP, 0)) AS nramv FROM CDWUSER.U_DECOMP_MSR d WHERE ( COB_DATE='2018-02-28' OR COB_DATE='2018-02-27' )  AND D.ccc_division = 'INSTITUTIONAL EQUITY DIVISION' AND D.CCC_BANKING_TRADING = 'TRADING' AND d.ISSUER_COUNTRY_CODE_DECOMP in ('BRA','PER','CHL','COL','MEX') group by COB_DATE, ISSUER_COUNTRY_CODE_DECOMP, ccc_business_area