SELECT COB_DATE, ISSUER_COUNTRY_CODE, CCC_BUSINESS_AREA ,sum(coalesce(D.USD_EQ_KAPPA, 0)) AS VEGA FROM CDWUSER.U_EQ_MSR d WHERE ( COB_DATE='2018-02-28' OR COB_DATE='2018-02-27' )  AND D.ccc_division = 'INSTITUTIONAL EQUITY DIVISION' AND D.CCC_BANKING_TRADING = 'TRADING' AND d.ISSUER_COUNTRY_CODE in ('BRA','PER','CHL','COL','MEX') group by COB_DATE, ISSUER_COUNTRY_CODE, CCC_BUSINESS_AREA