SELECT A.COB_DATE, A.COUNTRY_CD_OF_RISK, SUM(CAST(A.USD_EXPOSURE AS NUMERIC (15,5))) / 1000000 AS USD_NET_EXP FROM CDWUSER.U_DM_FIRMWIDE A WHERE COB_DATE >= '07/01/2016' AND COB_DATE <= '02/28/2018' AND CCC_TAPS_COMPANY in ('0302','0347','0853','4863','4043','6120','6837','6899','4044','0856','5869','7458') AND CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA NOT IN ('COMMODITIES') AND DIVISION_GROUP = 'ISG CORE' AND CCC_BUSINESS_AREA NOT IN ('LENDING') AND NOT ( CCC_BUSINESS_AREA = 'CREDIT-CORPORATES' AND ( CCC_PRODUCT_LINE IN ( 'NON INVSMT GRADE PRIMARY', 'NON IG PRIMARY - LOANS', 'PRIMARY - LOANS', 'NON IG PRIMARY - HY BOND', 'INVESTMENT GRADE PRIMARY','PRIMARY - BONDS','PRIMARY - IG BONDS','PRIMARY - NIG BONDS' ) OR CCC_STRATEGY IN ('NON IG PRIMARY - HY BOND', 'INVESTMENT GRADE PRIMARY') ) ) AND NOT ( CCC_BUSINESS_AREA IN ('EM CREDIT TRADING') AND ( CCC_PRODUCT_LINE IN ('EM CREDIT PRIMARY') OR CCC_STRATEGY IN ('EM CREDIT PRIMARY') ) ) AND CCC_BUSINESS_AREA NOT LIKE 'CPM%' AND CCC_BUSINESS_AREA NOT IN ('CREDIT DERIVATIVE PROD', 'DSP - CREDIT') AND COUNTRY_CD_OF_RISK IN ('ESP', 'ITA', 'GRC', 'PRT') AND NOT ( A.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' AND SPG_DESC IN ('CMBS IO REREMIC','CMBS IO','CMBS SECURITY','RMBS PRIME RESIDUAL','CMBS INDEX','CORPORATE INDEX') AND CCC_PRODUCT_LINE IN ('CRE LENDING', 'CREL BANK HFI', 'CRE LENDING SEC/HFS') ) AND CCC_PRODUCT_LINE NOT IN ('CREL BANK HFI', 'CRE LENDING SEC/HFS') AND PRODUCT_TYPE_CODE NOT IN ('WAREHOUSE') GROUP BY A.COB_DATE, A.COUNTRY_CD_OF_RISK