SELECT a.cob_date,         a.ccc_risk_manager_login,         (case when a.ccc_strategy_details in('EMERGING MARKETS ARB','INVENTORY OPT EM') then 'INVENTORY OPT EM' else a.ccc_strategy_details end) as ccc_strategy_details,        ( CASE             WHEN ( a.usd_eq_delta_decomp > 0                      ) THEN             Sum(Coalesce(a.usd_eq_delta_decomp, 0))                       END )                                   AS LNRAMV,         ( CASE             WHEN ( a.usd_eq_delta_decomp < 0                     ) THEN             Sum(Coalesce(a.usd_eq_delta_decomp, 0))                        END )                                   AS SNRAMV  FROM   CDWUSER.u_decomp_msr a      WHERE (COB_DATE = '2018-02-28' or COB_DATE = '2018-02-27') AND A.CCC_PL_REPORTING_REGION in ('AMERICAS') AND         a.ccc_division IN ( 'INSTITUTIONAL EQUITY DIVISION' )   AND (a.CCC_STRATEGY_DETAILS in ('EMERGING MARKETS DSP','EMERGING MARKETS1','INVENTORY OPT EM','MSET EM DSP') OR a.CCC_RISK_MANAGER_LOGIN IN ('christeb','limaleo','portek','eliasc','pessoat','pintoand'))  AND a.ISSUER_COUNTRY_REGION ='LATAM'   GROUP  BY a.cob_date,            a.ccc_risk_manager_login,            (case when a.ccc_strategy_details in('EMERGING MARKETS ARB','INVENTORY OPT EM') then 'INVENTORY OPT EM' else a.ccc_strategy_details end),           a.usd_eq_delta_decomp,            a.usd_cm_delta_decomp