SELECT FEED_SOURCE_NAME,cob_date,a.CURRENCY_OF_MEASURE,        sum(coalesce(a.USD_FX_DELTA,0)) + sum(coalesce(a.USD_FX_REALIZED,0) + coalesce(a.USD_FX_UNREALIZED,0) ) AS USD_FX FROM CDWUSER.U_FX_MSR a WHERE (COB_DATE = '2018-02-28' or COB_DATE = '2018-02-27') and   A.CCC_DIVISION IN ('INSTITUTIONAL EQUITY DIVISION') AND (a.CCC_BANKING_TRADING='TRADING' OR a.CCC_BANKING_TRADING IS NULL)   AND ( a.ccc_strategy_details IN ('EMERGING MARKETS ARB','INVENTORY OPT EM')               OR a.ccc_risk_manager_login IN ( 'christeb','limaleo','eliasc','portek','pintoand','pessoat'))               and a.CURRENCY_OF_MEASURE <> 'USD' AND a.ISSUER_COUNTRY_REGION ='LATAM'  GROUP BY FEED_SOURCE_NAME,COB_DATE,a.CURRENCY_OF_MEASURE having sum(coalesce(a.USD_FX_DELTA,0)) + sum(coalesce(a.USD_FX_REALIZED,0) + coalesce(a.USD_FX_UNREALIZED,0) )  <>0