SELECT COB_DATE, CASE WHEN A.CCC_PRODUCT_LINE IN ('DSP INDEX PRODUCTS TRADING','DSP INDEX PRODUCTS') THEN 'CREDIT-CORPORATES' ELSE A.CCC_BUSINESS_AREA END AS CCC_BUSINESS_AREA, SUM ( CASE WHEN ( A.CCC_BUSINESS_AREA NOT IN ('COMMODS FINANCING') AND A.CURRENCY_OF_MEASURE NOT IN ('UBD', 'USD') ) THEN COALESCE(A.USD_FX, 0) ELSE 0 END ) / 1000 AS FX_DELTA FROM CDWUSER.U_DM_FIRMWIDE A WHERE COB_DATE >= '07/01/2016' AND COB_DATE <= '02/28/2018' AND USD_FX <> 0 AND A.VAR_EXCL_FL <> 'Y' AND A.BU_RISK_RUN_CUSTOM1 <> 'STORAGE' AND CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA NOT IN ('COMMODITIES') AND DIVISION_GROUP = 'ISG CORE' AND CCC_BUSINESS_AREA NOT LIKE 'CPM%' AND NOT ( A.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' AND SPG_DESC IN ('CMBS IO REREMIC','CMBS IO','CMBS SECURITY','RMBS PRIME RESIDUAL','CMBS INDEX','CORPORATE INDEX') AND CCC_PRODUCT_LINE IN ('CRE LENDING', 'CREL BANK HFI', 'CRE LENDING SEC/HFS') ) GROUP BY COB_DATE, CASE WHEN A.CCC_PRODUCT_LINE IN ('DSP INDEX PRODUCTS TRADING','DSP INDEX PRODUCTS') THEN 'CREDIT-CORPORATES' ELSE A.CCC_BUSINESS_AREA END