SELECT A.COB_DATE, CASE WHEN A.CCC_PRODUCT_LINE IN ('DSP INDEX PRODUCTS TRADING','DSP INDEX PRODUCTS') THEN 'CREDIT-CORPORATES' ELSE A.CCC_BUSINESS_AREA END AS CCC_BUSINESS_AREA, CURRENCY_OF_MEASURE, SUM ( CASE WHEN COB_DATE >= '12/07/2015' THEN CAST(A.USD_PCA1 AS NUMERIC (15,5)) ELSE CAST (A.TEN_YEAR_EQ AS NUMERIC (15,5)) END ) AS TEN_YR FROM CDWUSER.U_DM_FIRMWIDE A WHERE COB_DATE >= '07/01/2016' AND COB_DATE <= '02/28/2018' AND CCC_PRODUCT_LINE NOT LIKE 'MS CVA MPE%' AND CCC_BUSINESS_AREA NOT LIKE 'CPM%' AND ( CCC_PRODUCT_LINE NOT LIKE 'NON IG PRIMARY%' OR CCC_STRATEGY NOT LIKE 'NON IG PRIMARY%' ) AND ( CCC_HIERARCHY_LEVEL9 NOT IN ( 'MS CVA MPE DERIVS CPM', 'MPE CVA RISK MGMT', 'MPE FVA RISK MGMT', 'MNE CVA RISK MGMT', 'MNE FVA RISK MGMT', 'CORE MPE CVA', 'MONOLINE MPE CVA', 'MS CVA MNE DERIVS FID' ) OR CCC_STRATEGY NOT IN ( 'MS CVA MPE DERIVS CPM', 'MPE CVA RISK MGMT', 'MPE FVA RISK MGMT', 'MNE CVA RISK MGMT', 'MNE FVA RISK MGMT', 'CORE MPE CVA', 'MONOLINE MPE CVA', 'MS CVA MNE DERIVS FID' ) ) AND CCC_BUSINESS_AREA NOT IN ('LENDING') AND CCC_BUSINESS_AREA NOT LIKE '%CVA%' AND VAR_EXCL_FL <> 'Y' AND VERTICAL_SYSTEM NOT IN ('PIPELINE_NY') AND ( CCC_PRODUCT_LINE NOT IN ( 'NON INVSMT GRADE PRIMARY', 'NON IG PRIMARY - LOANS', 'PRIMARY - LOANS', 'NON IG PRIMARY - HY BOND', 'INVESTMENT GRADE PRIMARY', 'PRIMARY - BONDS','PRIMARY - IG BONDS','PRIMARY - NIG BONDS' ) AND CCC_STRATEGY NOT IN ('NON IG PRIMARY - HY BOND', 'INVESTMENT GRADE PRIMARY') ) AND CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA NOT IN ('COMMODITIES') AND NOT ( CCC_BUSINESS_AREA IN ('EM CREDIT TRADING') AND ( CCC_PRODUCT_LINE IN ('EM CREDIT PRIMARY') OR CCC_STRATEGY IN ('EM CREDIT PRIMARY') ) ) AND A.CURRENCY_OF_MEASURE IN ('USD', 'EUR') AND NOT ( A.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' AND SPG_CATEGORY = 'WAREHOUSE_FINAL' ) AND NOT ( A.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' AND SPG_DESC IN ('CMBS IO REREMIC','CMBS IO','CMBS SECURITY','RMBS PRIME RESIDUAL','CMBS INDEX','CORPORATE INDEX') AND CCC_PRODUCT_LINE IN ('CRE LENDING', 'CREL BANK HFI', 'CRE LENDING SEC/HFS') ) GROUP BY A.COB_DATE, CASE WHEN A.CCC_PRODUCT_LINE IN ('DSP INDEX PRODUCTS TRADING','DSP INDEX PRODUCTS') THEN 'CREDIT-CORPORATES' ELSE A.CCC_BUSINESS_AREA END, CURRENCY_OF_MEASURE