SELECT A.COB_DATE, CASE WHEN A.SPG_DESC = 'AGENCY CMBS IO' THEN 'AGENCY_FINAL' WHEN ( SPG_DESC IN ( 'CMBS IO REREMIC','CMBS IO','CMBS SECURITY', 'RMBS PRIME RESIDUAL','CMBS INDEX','CORPORATE INDEX' ) AND CCC_PRODUCT_LINE IN ('CRE LENDING', 'CREL BANK HFI', 'CRE LENDING SEC/HFS') ) THEN 'CRE_LENDING_HEDGES' WHEN A.SPG_CATEGORY IN ('RMBS SUB PRIME SECOND PAY') THEN 'NON_AGENCY_RESI_FINAL' ELSE SPG_CATEGORY END AS SPG_CATEGORY, SUM(CAST(A.USD_EXPOSURE AS NUMERIC (15,5))) /1000 AS NET_EXPOSURE FROM CDWUSER.U_DM_FIRMWIDE A WHERE COB_DATE >= '07/01/2016' AND COB_DATE <= '02/28/2018' AND CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA NOT IN ('COMMODITIES') AND CCC_PRODUCT_LINE NOT LIKE ('%CVA%') AND ( VERTICAL_SYSTEM IN ('SPG_NY', 'SPG_LN', 'SPG_TK', 'MBS_NY', 'MUNI_NY') OR ( VERTICAL_SYSTEM LIKE '%STS_%' AND CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' ) OR ( VERTICAL_SYSTEM LIKE '%C1%' AND CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' ) OR UPPER(SPG_DESC) LIKE '%ABS%' OR UPPER(SPG_DESC) LIKE '%CMBS%' OR UPPER(SPG_DESC) LIKE '%COLONNADE%' OR PRODUCT_TYPE_CODE LIKE 'ARS%' OR UPPER(SPG_DESC) LIKE '%RMBS%' OR UPPER(SPG_DESC) LIKE '%RPX%' OR UPPER(SPG_DESC) LIKE '%WAREHOUSE%' OR PRODUCT_TYPE_CODE = 'RPX' ) AND PRODUCT_TYPE_CODE <> 'MUNI' AND CVA_FL <> 'Y' AND CCC_BUSINESS_AREA NOT LIKE 'CPM%' AND NOT ( SPG_DESC = 'CORPORATE BONDS' AND CCC_PL_REPORTING_REGION = 'JAPAN' ) GROUP BY A.COB_DATE, CASE WHEN A.SPG_DESC = 'AGENCY CMBS IO' THEN 'AGENCY_FINAL' WHEN ( SPG_DESC IN ( 'CMBS IO REREMIC','CMBS IO','CMBS SECURITY', 'RMBS PRIME RESIDUAL','CMBS INDEX','CORPORATE INDEX' ) AND CCC_PRODUCT_LINE IN ('CRE LENDING', 'CREL BANK HFI', 'CRE LENDING SEC/HFS') ) THEN 'CRE_LENDING_HEDGES' WHEN A.SPG_CATEGORY IN ('RMBS SUB PRIME SECOND PAY') THEN 'NON_AGENCY_RESI_FINAL' ELSE SPG_CATEGORY END