SELECT A.COB_DATE, CASE WHEN A.CCC_PRODUCT_LINE IN ('DSP INDEX PRODUCTS TRADING','DSP INDEX PRODUCTS') THEN 'CREDIT-CORPORATES' ELSE A.CCC_BUSINESS_AREA END AS CCC_BUSINESS_AREA, SUM(cast(A.USD_EXPOSURE as numeric(15,5))) / 1000000 AS USD_NET_EXP FROM CDWUSER.U_DM_FIRMWIDE A WHERE COB_DATE >= '07/01/2016' AND COB_DATE <= '02/28/2018' AND A.VAR_EXCL_FL <> 'Y' AND A.CVA_FL <> 'Y' AND A.COUNTRY_OF_RISK_TIER = '2' AND A.VERTICAL_SYSTEM NOT IN ('PIPELINE_NY') AND CCC_DIVISION = 'FIXED INCOME DIVISION' AND DIVISION_GROUP = 'ISG CORE' AND ( CCC_BUSINESS_AREA NOT IN ('LENDING') ) AND NOT ( CCC_BUSINESS_AREA = 'CREDIT-CORPORATES' AND ( CCC_PRODUCT_LINE IN ( 'PRIMARY - BONDS','PRIMARY - IG BONDS','PRIMARY - NIG BONDS', 'NON INVSMT GRADE PRIMARY', 'NON IG PRIMARY - LOANS', 'PRIMARY - LOANS', 'NON IG PRIMARY - HY BOND', 'INVESTMENT GRADE PRIMARY' ) OR CCC_STRATEGY IN ('NON IG PRIMARY - HY BOND', 'INVESTMENT GRADE PRIMARY') ) ) AND NOT ( CCC_BUSINESS_AREA IN ('EM CREDIT TRADING') AND ( CCC_PRODUCT_LINE IN ('EM CREDIT PRIMARY') OR CCC_STRATEGY IN ('EM CREDIT PRIMARY') ) ) AND CCC_BUSINESS_AREA NOT LIKE 'CPM%' AND NOT ( A.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' AND SPG_DESC IN ('CMBS IO REREMIC','CMBS IO','CMBS SECURITY','RMBS PRIME RESIDUAL','CMBS INDEX','CORPORATE INDEX') AND CCC_PRODUCT_LINE IN ('CRE LENDING', 'CREL BANK HFI', 'CRE LENDING SEC/HFS') ) GROUP BY A.COB_DATE, CASE WHEN A.CCC_PRODUCT_LINE IN ('DSP INDEX PRODUCTS TRADING','DSP INDEX PRODUCTS') THEN 'CREDIT-CORPORATES' ELSE A.CCC_BUSINESS_AREA END