select A.COB_DATE, A.ISSUER_COUNTRY_CODE, A.CCC_PRODUCT_LINE, CASE WHEN A.CCC_TAPS_COMPANY IN ('0201','0103','0205','0206','0530','5924') THEN 'MSCO' WHEN A.PARENT_LEGAL_ENTITY IN ('0302(G)') THEN 'MSIP' ELSE A.CCC_TAPS_COMPANY END AS LE, sum(coalesce(a.slide_kappa_pls_20_usd,0)) AS EQ_Vol_Slide_Plus_20 FROM cdwuser.U_EXP_MSR A where a.cob_date >= '2018-02-14' and a.cob_date <= '2018-02-28' AND A.CCC_DIVISION IN ('INSTITUTIONAL EQUITY DIVISION') AND ( a.CCC_TAPS_COMPANY IN ('0201','0103','0205','0206','0530','5924') or A.PARENT_LEGAL_ENTITY IN ('0302(G)') ) and a.CCC_PL_REPORTING_REGION = 'AMERICAS' GROUP BY A.COB_DATE, A.ISSUER_COUNTRY_CODE, A.CCC_PRODUCT_LINE, LE