select a.cob_date, 'IED' as business, A.CCC_PL_REPORTING_REGION, CASE WHEN A.CCC_TAPS_COMPANY IN ('0201','0103','0205','0206','0530','5924') THEN 'MSCO' WHEN A.CCC_TAPS_COMPANY IN ('1928') THEN 'MSHEDGECO' WHEN A.PARENT_LEGAL_ENTITY IN ('0302(G)') THEN 'MSIP' ELSE A.CCC_TAPS_COMPANY END AS LE, sum(coalesce(A.SLIDE_EQ_MIN_10_USD,0)) as SLIDE_EQ_MIN_10_USD, sum(coalesce(A.SLIDE_EQ_PLS_10_USD,0)) as SLIDE_EQ_PLS_10_USD from cdwuser.u_exp_msr a where a.cob_date >= '2018-02-14' and a.cob_date <= '2018-02-28' AND A.CCC_DIVISION IN ('INSTITUTIONAL EQUITY DIVISION') AND (a.CCC_TAPS_COMPANY IN ('0201','0103','0205','0206','0530','5924','1928') or A.PARENT_LEGAL_ENTITY IN ('0302(G)')) group by a.cob_date, A.CCC_PL_REPORTING_REGION, CASE WHEN A.CCC_TAPS_COMPANY IN ('0201','0103','0205','0206','0530','5924') THEN 'MSCO' WHEN A.CCC_TAPS_COMPANY IN ('1928') THEN 'MSHEDGECO' WHEN A.PARENT_LEGAL_ENTITY IN ('0302(G)') THEN 'MSIP' ELSE A.CCC_TAPS_COMPANY END