SELECT a.cob_date ,a.ccc_business_area ,a.ccc_product_line, CASE WHEN PARENT_LEGAL_ENTITY = '0302(G)' THEN 'MSIP' ELSE 'Non-MSIP' END AS LE, CASE WHEN CCC_BUSINESS_AREA IN ('OIL LIQUIDS') THEN 'OIL & PRODUCTS' WHEN CCC_BUSINESS_AREA IN ('INVESTOR BUSINESS') AND A.CCC_PRODUCT_LINE IN ('IB INDEX') THEN 'COMMOD INDEX' WHEN CCC_BUSINESS_AREA IN ('INVESTOR BUSINESS') AND A.CCC_PRODUCT_LINE IN ('IB STRUCTURED') THEN 'COMMOD EXOTICS' WHEN CCC_BUSINESS_AREA in ('NA ELECTRICITYNATURAL GAS') THEN 'NA POWER & GAS' ELSE CCC_PRODUCT_LINE END AS GROUPED_BREAKDOWN ,SUM(CASE WHEN PRODUCT_TYPE_CODE not in ('EQUITY INDEX','CREDIT') THEN coalesce(USD_CM_DELTA,0) ELSE 0 END) as delta FROM CDWUSER.U_exp_msr a WHERE COB_DATE in ('2018-02-28','2018-01-31','2017-12-29','2017-11-30','2017-10-31','2017-09-29','2017-08-31','2017-07-31','2017-06-30','2017-05-31','2017-04-28','2017-03-31','2017-02-28') AND var_excl_fl <> 'Y' AND FEED_SOURCE_NAME = 'CORISK' AND ccc_business_area NOT IN ('CREDIT') AND PRODUCT_TYPE_CODE not in ('CASH','MISC','ERROR','TBD','INTEREST RATE','CURRENCY') AND feed_source_name = 'CORISK' AND ccc_banking_trading = 'TRADING' AND a.CCC_PL_REPORTING_REGION = 'AMERICAS' GROUP BY 1,2,3,4,5