select sum(a.USD_PV10_BENCH) as PV10, a.ISSUER_COUNTRY_CODE,COB_DATE,reference_entity_name,sum(USD_NET_EXPOSURE) as Exposure,CCC_BUSINESS_AREA,position_ult_issuer_party_darwin_name,issuer_country_em_flag,
case when a.ISSUER_COUNTRY_CODE in ('RUS','KAZ','UKR','AZE','BLR','GEO') then 'CIS' when a.ISSUER_COUNTRY_CODE in ('CHN','HKG','IND','IDN','KOR','MYS','PHL','SGP','TWN','THA','VNM','PAK','LKA','MNG','BGD','MAC') then 'Asia' when a.ISSUER_COUNTRY_CODE in ('BGR', 'HRV', 'BIH', 'CZE', 'EST', 'HUN', 'ISL', 'LVA', 'POL', 'ROM', 'SRB', 'SVK', 'SVN', 'TUR', 'BHR', 'CIV', 'EGY', 'ISR', 'KWT', 'LBR', 'MAR', 'NGA', 'QAT', 'SAU', 'ZAF', 'ARE', 'GHA', 'IRQ', 'LBN', 'LTU', 'ROU', 'TUN', 'KEN', 'ZMB', 'GAB', 'SEN', 'JOR', 'TZA', 'RWA', 'NAM', 'ARM', 'ETH', 'AGO', 'MNE', 'CMR', 'MOZ') then 'EMEA' when a.ISSUER_COUNTRY_CODE in ('ARG','BRA','CHL','COL','CRI','JAM','MEX','PER','VEN','DOM','ECU','GTM','PAN','URY','SLV','BRB','GUY','HND','PRY','TTO','BHS') then 'LatAm' else 'Other' end as Country_Grouping

from CDWUSER.U_DM_CC a
 where a.COB_DATE >= ('2018-02-14') and a.COB_DATE <= ('2018-02-28') AND a.CCC_TAPS_COMPANY ='0302' AND a.CCC_DIVISION IN ('FIXED INCOME DIVISION') AND a.CCC_BUSINESS_AREA NOT IN ('NON CORE','CPM','LENDING') AND a.CCC_BANKING_TRADING = 'TRADING' AND (a.CCC_PRODUCT_LINE NOT IN ('DISTRESSED TRADING') OR a.PRODUCT_TYPE_CODE IN ('CDSOPTIDX','CRDINDEX','LOANINDEX')) AND ((a.FID1_SENIORITY NOT IN ('AT1','SUBT1','SUBUT2') OR a.ISSUER_COUNTRY_CODE NOT IN ('DNK','BMU','CYM','AUS','AUT','BEL','CAN','CHE','DEU','ESP','FIN','FRA','GBR','GRC','IRL','ITA','JPN','LUX','NLD','NOR','NZL','PRT','SWE','USA','JEY','GGY','SUP','XS','XCI','VGB','CYP')) OR a.PRODUCT_TYPE_CODE NOT IN ('BOND','BONDFUT','BONDFUTOPT','BONDIL','BONDOPT','FRN','PREF')) AND a.VERTICAL_SYSTEM NOT LIKE '%SPG%' AND (CURRENCY_OF_MEASURE NOT in ('AUD','CAD','CHF','DKK','EUR','GBP','JPY','NOK','NZD','SEK','USD','UBD') AND fid1_industry_name_level1 = 'SOVEREIGN') AND USD_NET_EXPOSURE is not null AND USD_NET_EXPOSURE <> 0 AND book not in ('OBTRS')
AND (a.FID1_INDUSTRY_NAME_LEVEL1 NOT IN ('SOVEREIGN', 'GOVERNMENT SPONSORED') 
OR a.ISSUER_COUNTRY_CODE NOT IN ('DNK','BMU','CYM','AUS','AUT','BEL','CAN','CHE','DEU','ESP','FIN','FRA','GBR','GRC','IRL','ITA','JPN','LUX','NLD','NOR','NZL','PRT','SWE','USA','JEY','GGY','SUP','XS','XCI','VGB','CYP')) group by a.ISSUER_COUNTRY_CODE,COB_DATE,PRODUCT_TYPE_CODE,reference_entity_name,CCC_BUSINESS_AREA,position_ult_issuer_party_darwin_name,issuer_country_em_flag