select cob_date, case when ccc_division in ('INSTITUTIONAL EQUITY DIVISION') then 'IED' when (ccc_division in ('FIXED INCOME DIVISION') and ccc_business_area in ('FXEM MACRO TRADING','EM CREDIT TRADING')) then 'FXEM' when (ccc_division in ('FIXED INCOME DIVISION') and ccc_business_area in ('LIQUID FLOW RATES','STRUCTURED RATES')) then 'IR' when (ccc_division in ('FIXED INCOME DIVISION') and ccc_business_area in ('CREDIT-CORPORATES','SECURITIZED PRODUCTS GRP')) then 'CC-SPG' when (ccc_division in ('FIXED INCOME DIVISION') and ccc_business_area in ('DSP - CREDIT')) then 'DSP Cr' when ccc_division in ('FIXED INCOME DIVISION') then 'Others' else 'Other' end as hierarchy, sum(case when is_uk_group = 'Y' then usd_pv10_bench else 0 end) as pv10_ukg, sum(case when ccc_pl_reporting_region = 'EMEA' then usd_pv10_bench else 0 end) as pv10_emea,
sum(case when ccc_pl_reporting_region in ('ASIA PACIFIC','JAPAN') then usd_pv10_bench else 0 end) as pv10_asia from cdwuser.U_DM_CC a where a.COB_DATE in ('2018-02-28','2018-02-27') and (a.USD_PV10_BENCH <> 0 and a.USD_PV10_BENCH is not null) AND a.CCC_BUSINESS_AREA NOT IN ('NON CORE','CPM','LENDING') AND a.CCC_BANKING_TRADING = 'TRADING' AND (a.CCC_PRODUCT_LINE NOT IN ('DISTRESSED TRADING') OR a.PRODUCT_TYPE_CODE IN ('CDSOPTIDX','CRDINDEX','LOANINDEX')) AND ((a.FID1_SENIORITY NOT IN ('AT1','SUBT1','SUBUT2') OR a.ISSUER_COUNTRY_CODE NOT IN ('DNK','BMU','CYM','AUS','AUT','BEL','CAN','CHE','DEU','ESP','FIN','FRA','GBR','GRC','IRL','ITA','JPN','LUX','NLD','NOR','NZL','PRT','SWE','USA','JEY','GGY','SUP','XS','XCI','VGB','CYP')) OR a.PRODUCT_TYPE_CODE NOT IN ('BOND','BONDFUT','BONDFUTOPT','BONDIL','BONDOPT','FRN','PREF')) AND a.VERTICAL_SYSTEM NOT LIKE '%SPG%' group by cob_date, case when ccc_division in ('INSTITUTIONAL EQUITY DIVISION') then 'IED' when (ccc_division in ('FIXED INCOME DIVISION') and ccc_business_area in ('FXEM MACRO TRADING','EM CREDIT TRADING')) then 'FXEM' when (ccc_division in ('FIXED INCOME DIVISION') and ccc_business_area in ('LIQUID FLOW RATES','STRUCTURED RATES')) then 'IR' when (ccc_division in ('FIXED INCOME DIVISION') and ccc_business_area in ('CREDIT-CORPORATES','SECURITIZED PRODUCTS GRP')) then 'CC-SPG' when (ccc_division in ('FIXED INCOME DIVISION') and ccc_business_area in ('DSP - CREDIT')) then 'DSP Cr' when ccc_division in ('FIXED INCOME DIVISION') then 'Others' else 'Other' end