SELECT b.COB_DATE, b.EXECUTIVE_MODEL, b.SECURITY_DESCRIPTION, b.EXPIRATION_DATE, 0 AS VEGA, b.SCENARIO_NAME, CASE WHEN b.SCENARIO_NAME IN ('V+.2') THEN SUM(b.POS_TV_MINUS_BASE) ELSE 0 END AS V20, CASE WHEN b.SCENARIO_NAME IN ('V+.1') THEN SUM(b.POS_TV_MINUS_BASE) ELSE 0 END AS V10, CASE WHEN b.SCENARIO_NAME IN ('V+.05') THEN SUM(b.POS_TV_MINUS_BASE) ELSE 0 END AS V05 FROM cdwuser.U_IED_SURFACE b where B.COB_DATE IN('2018-02-28', '2018-02-27') AND b.CCC_BUSINESS_AREA = 'DERIVATIVES' AND b.CCC_PL_REPORTING_REGION = 'AMERICAS' AND b.SCENARIO_NAME IN ('V+.2','V+.1','V+.05')  AND b.CCC_RISK_MANAGER_LOGIN = 'derbrann' GROUP BY b.COB_DATE, b.EXECUTIVE_MODEL, b.SECURITY_DESCRIPTION,b.EXPIRATION_DATE, b.SCENARIO_NAME   UNION ALL  SELECT b.COB_DATE, b.EXECUTIVE_MODEL, b.SECURITY_DESCRIPTION, b.EXPIRATION_DATE, SUM(b.USD_VEGA) AS VEGA, '' AS SCENARIO_NAME, 0 AS V20, 0 AS V10, 0 AS V05 FROM cdwuser.U_IED_VIEW b where B.COB_DATE IN('2018-02-28', '2018-02-27') AND b.CCC_BUSINESS_AREA = 'DERIVATIVES' AND b.CCC_PL_REPORTING_REGION = 'AMERICAS'     AND b.CCC_RISK_MANAGER_LOGIN = 'derbrann' GROUP BY b.COB_DATE, b.EXECUTIVE_MODEL, b.SECURITY_DESCRIPTION,b.EXPIRATION_DATE