select a.COB_DATE, a.CCC_PL_REPORTING_REGION, case when a.BOOK in ('WAREHOUSE_SMALL_BUSINESS', 'WAREHOUSE_CONSUMER') then 'WAREHOUSE_CONSUMER' when a.BOOK in ('WAREHOUSE_SFR', 'WAREHOUSE_RESI') then 'WAREHOUSE_RESI' when a.BOOK in ('WAREHOUSE_MUNI', 'WAREHOUSE_STUDENTLOAN') then 'WAREHOUSE_STUDENTLOAN' else a.BOOK end as BOOK, a.PRODUCT_TYPE_CODE, sum ((a.USD_IR_UNIFIED_PV01) :: numeric(15,5)) AS PV01, sum ((a.USD_PV01SPRD) :: numeric(15,5)) as SPV01, sum ((a.USD_NOTIONAL) :: numeric(15,5))*1000 as NOTIONAL, sum ((a.USD_EXPOSURE) :: numeric(15,5))*1000 AS NET FROM CDWUSER.U_DM_WM a where a.COB_DATE in ( '2018-02-28', '2018-02-27', '2018-01-31', '2017-12-29', '2017-11-30', '2017-10-31' ) and a.CCC_BANKING_TRADING = 'BANKING' and a.CCC_TAPS_COMPANY = '1633' and a.CCC_DIVISION in ('FIXED INCOME DIVISION','NON CORE') and a.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' and a.CCC_PRODUCT_LINE = 'WAREHOUSE' and a.PRODUCT_TYPE_CODE = 'WAREHOUSE' group by a.COB_DATE, a.CCC_PL_REPORTING_REGION, case when a.BOOK in ('WAREHOUSE_SMALL_BUSINESS', 'WAREHOUSE_CONSUMER') then 'WAREHOUSE_CONSUMER' when a.BOOK in ('WAREHOUSE_SFR', 'WAREHOUSE_RESI') then 'WAREHOUSE_RESI' when a.BOOK in ('WAREHOUSE_MUNI', 'WAREHOUSE_STUDENTLOAN') then 'WAREHOUSE_STUDENTLOAN' else a.BOOK end, a.PRODUCT_TYPE_CODE