SELECT COB_DATE, CASE WHEN a.CCC_BUSINESS_AREA = 'LENDING' THEN 'LENDING' WHEN A.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' THEN 'CRE' WHEN A. CCC_BUSINESS_AREA = 'CPM' THEN 'CPM' WHEN A.CCC_PRODUCT_LINE LIKE '%PAR LOANS TRADING%' THEN 'PAR LOAN' WHEN A.BOOK LIKE '%ELTH FVO%' OR A.BOOK LIKE '%ELTH HFS%' THEN 'LENDING' ELSE 'OTHERS - Non VaR' END AS BUSINESS_AREA, CCC_STRATEGY, book, PRODUCT_TYPE_CODE, CASE WHEN (UPPER (A.BOOK) LIKE '%WORKOUT%') OR A.BOOK IN ('PMGPB', 'EVREU') THEN 'DISTRESSED' WHEN A.CCC_STRATEGY IN ('CORPORATE LOAN STRATEGY') THEN 'CORPORATE LOAN STRATEGY' WHEN A.CCC_STRATEGY IN ( 'PROJECT FINANCE' ) THEN 'PROJECT FINANCE' WHEN A.BOOK LIKE '%ELTH FVO%' OR A.BOOK LIKE '%ELTH HFS%' THEN 'Event-ELTH' WHEN (A.CCC_STRATEGY IN ('NON IG PRIMARY - LOANS', 'PROJECT FINANCE') OR A.CCC_PRODUCT_LINE IN ('NON IG PRIMARY - LOANS', 'PRIMARY - LOANS')) AND (A.BOOK LIKE '%ELTH FVO%' OR A.BOOK LIKE '%ELTH HFS%' OR BOOK = 'LJV-PROJECT FINANCE-NA-HFS-IMD-MSBANK-PFMDA') THEN 'Lending - UNHEDGEABLES' WHEN (A.CCC_STRATEGY IN ('HELD FOR SALE', 'HELD FOR SALE IMD', 'RELATIONSHIP TDG UNHEDGBL') OR A.CCC_PRODUCT_LINE IN ('HELD FOR SALE', 'HELD FOR SALE IMD', 'RELATIONSHIP TDG UNHEDGBL')) AND (UPPER (A.BOOK) NOT LIKE '%-HEDGEABLE-%') THEN 'Lending - UNHEDGEABLES' WHEN (A.CCC_STRATEGY IN ('HELD FOR SALE', 'HELD FOR SALE IMD', 'HFI HEDGING', 'HFS HEDGING', 'RELATIONSHIP IMD', 'RELATIONSHIP TRADING') OR A.CCC_PRODUCT_LINE IN ('HELD FOR SALE', 'HELD FOR SALE IMD', 'HFI HEDGING', 'HFS HEDGING', 'RELATIONSHIP IMD', 'RELATIONSHIP TRADING')) AND (UPPER (A.BOOK) NOT LIKE '%-UNHEDGEABLE-%') THEN 'Lending - HEDGEABLES' ELSE 'OTHER' END AS LIMIT_DEFINITION, SUM ((a.USD_PV01SPRD) :: numeric(15,5)) as USD_PV01SPRD FROM CDWUSER.U_DM_WM A WHERE a.COB_DATE IN ( '2018-02-28', '2018-01-31', '2017-12-29', '2017-11-30', '2017-10-31', '2017-09-29' ) AND A.vertical_system not like 'PIPELINE%' AND A.VAR_EXCL_FL<> 'Y' and a.CCC_TAPS_COMPANY = '1633' AND CCC_STRATEGY NOT LIKE '%HELD FOR INVESTMENT%' AND a.CCC_PRODUCT_LINE NOT IN ('WAREHOUSE') AND (CCC_STRATEGY IN ('HFI HEDGING') OR a.BOOK NOT LIKE '%HFI%') GROUP BY CASE WHEN a.CCC_BUSINESS_AREA = 'LENDING' THEN 'LENDING' WHEN A.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' THEN 'CRE' WHEN A. CCC_BUSINESS_AREA = 'CPM' THEN 'CPM' when A.CCC_PRODUCT_LINE LIKE '%PAR LOANS TRADING%' THEN 'PAR LOAN' WHEN A.BOOK LIKE '%ELTH FVO%' OR A.BOOK LIKE '%ELTH HFS%' THEN 'LENDING' ELSE 'OTHERS - Non VaR' END, a.COB_DATE, a.CCC_STRATEGY, a.CCC_PRODUCT_LINE, a.book, A.PRODUCT_TYPE_CODE, MRD_RATING