SELECT COB_DATE, MAX(USD_PV10_BENCH) AS MAX_SINGLE_BPV10, MAX(USD_NET_EXPOSURE) AS MAX_SINGLE_EXPOSURE FROM ( SELECT a.COB_DATE, a.CCC_PRODUCT_LINE, A.POSITION_ISSUER_PARTY_DARWIN_NAME, ABS(SUM((A.USD_PV10_BENCH) :: numeric(15,5))) AS USD_PV10_BENCH, ABS(SUM((A.USD_NET_EXPOSURE) :: numeric(15,5))) AS USD_NET_EXPOSURE FROM cdwuser.U_dm_WM a WHERE a.COB_DATE IN ( '2018-02-28', '2018-01-31', '2017-12-29', '2017-11-30', '2017-10-31', '2017-09-29' ) AND A.CCC_TAPS_COMPANY = '1633' AND A.CCC_PRODUCT_LINE IN ('PAR LOANS TRADING') GROUP BY a.COB_DATE, a.CCC_PRODUCT_LINE, A.POSITION_ISSUER_PARTY_DARWIN_NAME ) B GROUP BY COB_DATE