Select current.cob_date, current.REFERENCE_INDEX_ENTITY_NAME, current.SPV01, coalesce(current.SPV01,0) - coalesce(previous.SPV01,0) as SPV01_CHG FROM (SELECT A.COB_DATE, A.REFERENCE_INDEX_ENTITY_NAME, A.PRODUCT_TYPE_CODE, sum((A.USD_PV01SPRD) :: numeric(15,5)) as SPV01 FROM CDWUSER.U_DM_WM A WHERE A.COB_DATE IN ( '2018-02-28' ) AND A.CCC_TAPS_COMPANY = '1633' and A.CCC_DIVISION in ('FIXED INCOME DIVISION','NON CORE') and A.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' and A.CCC_PRODUCT_LINE in ('CRE LENDING', 'CREL BANK HFI', 'CRE LENDING SEC/HFS') and A.product_type_code = 'CRDINDEX' GROUP BY A.COB_DATE, A.REFERENCE_INDEX_ENTITY_NAME, A.PRODUCT_TYPE_CODE) current full outer join (SELECT A.COB_DATE, A.REFERENCE_INDEX_ENTITY_NAME, A.PRODUCT_TYPE_CODE, sum((A.USD_PV01SPRD) :: numeric(15,5)) as SPV01 FROM CDWUSER.U_DM_WM A WHERE A.COB_DATE IN ( '2018-01-31' ) AND A.CCC_TAPS_COMPANY = '1633' and A.CCC_DIVISION in ('FIXED INCOME DIVISION','NON CORE') and A.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' and A.CCC_PRODUCT_LINE in ('CRE LENDING', 'CREL BANK HFI', 'CRE LENDING SEC/HFS') and A.product_type_code = 'CRDINDEX' GROUP BY A.COB_DATE, A.REFERENCE_INDEX_ENTITY_NAME, A.PRODUCT_TYPE_CODE) previous on current.REFERENCE_INDEX_ENTITY_NAME = previous.REFERENCE_INDEX_ENTITY_NAME and current.PRODUCT_TYPE_CODE = previous.PRODUCT_TYPE_CODE