select x.COB_DATE , SUM(x.USD_MARKET_VALUE) ::numeric(15,5) /1000 AS MV from cdwuser.U_DM_WM x WHERE x.COB_DATE in ( '2018-02-28', '2018-02-27' ) AND (x.CCC_STRATEGY = 'HELD FOR INVESTMENT - (HFI)' OR X.CCC_DIVISION = 'HELD FOR INVESTMENT - (HFI)') AND x.FACILITY_TYPE = 'RATELOCK' group by x.cob_date